document.addEventListener('DOMContentLoaded',function(){function initLazyLoadPlaceholders(){const placeholders=document.querySelectorAll('.lazy-load-placeholder');if(!('IntersectionObserver' in window)||placeholders.length===0)return;const lazyLoadCallback=(entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){const placeholder=entry.target;const img=document.createElement('img');img.src=placeholder.dataset.src;img.alt=placeholder.dataset.alt;img.className=placeholder.dataset.class;placeholder.parentNode.replaceChild(img,placeholder);observer.unobserve(img)}})};const lazyLoadObserver=new IntersectionObserver(lazyLoadCallback,{rootMargin:'1200px 0px'});placeholders.forEach(placeholder=>{lazyLoadObserver.observe(placeholder)})}
function initFifuTitleFix(){const fifuImages=document.querySelectorAll('img[fifu-featured]');if(fifuImages.length===0)return;const observer=new MutationObserver(mutations=>{mutations.forEach(mutation=>{if(mutation.type==='attributes'&&mutation.attributeName==='title'){const target=mutation.target;if(target.hasAttribute('title')){target.removeAttribute('title')}}})});fifuImages.forEach(img=>{if(img.hasAttribute('title')){img.removeAttribute('title')}
observer.observe(img,{attributes:!0,attributeFilter:['title']})})}
initLazyLoadPlaceholders();window.addEventListener('load',initFifuTitleFix)})