document.addEventListener("DOMContentLoaded",function(){const el=document.getElementById("i-have-a-tooltip");if(!el)return;const tooltip=document.createElement("div");tooltip.className="tooltip-box";tooltip.textContent=el.getAttribute("data-description");document.body.appendChild(tooltip);el.addEventListener("mouseenter",(e)=>{tooltip.style.left=e.pageX+10+"px";tooltip.style.top=e.pageY+10+"px";tooltip.style.opacity=1});el.addEventListener("mousemove",(e)=>{tooltip.style.left=e.pageX+10+"px";tooltip.style.top=e.pageY+10+"px"});el.addEventListener("mouseleave",()=>{tooltip.style.opacity=0})})